import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);

    // Create preview
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setError('Please select an image first');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      console.log('Complete response:', JSON.stringify(response.data, null, 2));

      if (!response.data || !response.data.result || !response.data.result.categories) {
        throw new Error('Invalid response format from server');
      }

      setCategories(response.data.result.categories);

    } catch (err) {
      console.error('Full error details:', err);
      console.error('Error response data:', err.response?.data);
      setError('Failed to analyze image. Please try again. Error: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Image Analysis</h1>

      <div className="mb-6">
        <input
          type="file"
          onChange={handleFileSelect}
          accept="image/*"
          className="mb-4"
        />

        {preview && (
          <div className="mb-4">
            <img src={preview} alt="Preview" className="max-w-md rounded-lg shadow-lg" />
          </div>
        )}

        <button
          onClick={handleUpload}
          disabled={!selectedFile || loading}
          className={`px-4 py-2 rounded-lg ${
            !selectedFile || loading
              ? 'bg-gray-300 cursor-not-allowed'
              : 'bg-blue-500 hover:bg-blue-600 text-white'
          }`}
        >
          {loading ? 'Analyzing...' : 'Analyze Image'}
        </button>
      </div>

      {error && (
        <div className="text-red-500 mb-4">{error}</div>
      )}

      {categories.length > 0 && (
        <div>
          <h2 className="text-xl font-semibold mb-4">Image Categories</h2>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2">
            {categories.map((category, index) => (
              <div 
                key={index}
                className="bg-gray-100 p-3 rounded-lg"
              >
                <div className="font-medium text-lg">{category.name.en}</div>
                <div className="text-sm text-gray-600">
                  Confidence: {Math.round(category.confidence)}%
                </div>
                {category.parents && category.parents.length > 0 && (
                  <div className="text-xs text-gray-500 mt-1">
                    Category: {category.parents.map(p => p.en).join(' > ')}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
