import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
    const [selectedFile, setSelectedFile] = useState(null);
    const [preview, setPreview] = useState(null);
    const [results, setResults] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');
    const [imageUrl, setImageUrl] = useState('');
    const [history, setHistory] = useState([]);
    const [saved, setSaved] = useState([]);
    const [activeTab, setActiveTab] = useState('upload');
    const [showLogin, setShowLogin] = useState(false);
    const [showRegister, setShowRegister] = useState(false);
    const [user, setUser] = useState(null);
    const [loginForm, setLoginForm] = useState({ username: '', password: '' });
    const [registerForm, setRegisterForm] = useState({ username: '', email: '', password: '' });

    // Theme effect
    useEffect(() => {
        document.documentElement.setAttribute('data-theme', theme);
        localStorage.setItem('theme', theme);
    }, [theme]);

    const toggleTheme = () => {
        setTheme(theme === 'light' ? 'dark' : 'light');
    };

    // Check for existing token on load
    useEffect(() => {
        const token = localStorage.getItem('token');
        const userData = localStorage.getItem('user');
        if (token && userData) {
            setUser(JSON.parse(userData));
            fetchUserData();
        }
    }, []);

    const fetchUserData = async () => {
        if (user) {
            try {
                await Promise.all([
                    fetchHistory(),
                    fetchSaved()
                ]);
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        }
    };

    const fetchHistory = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) return;

            const response = await axios.get('http://localhost:5000/history', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setHistory(response.data);
        } catch (error) {
            setError('Failed to fetch history');
        }
    };

    const fetchSaved = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) return;

            const response = await axios.get('http://localhost:5000/saved', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setSaved(response.data);
        } catch (error) {
            setError('Failed to fetch saved analyses');
        }
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/login', loginForm);
            const { token, user } = response.data;

            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(user));

            setUser(user);
            setShowLogin(false);
            setLoginForm({ username: '', password: '' });
            fetchUserData();
        } catch (error) {
            setError(error.response?.data?.error || 'Login failed');
        }
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/register', registerForm);
            const { token, user } = response.data;

            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(user));

            setUser(user);
            setShowRegister(false);
            setRegisterForm({ username: '', email: '', password: '' });
            fetchUserData();
        } catch (error) {
            setError(error.response?.data?.error || 'Registration failed');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setUser(null);
        setHistory([]);
        setSaved([]);
        setActiveTab('upload');
    };

    const toggleSave = async (id) => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('Please log in to save analyses');
                return;
            }

            await axios.post(`http://localhost:5000/toggle-save/${id}`, {}, {
                headers: { Authorization: `Bearer ${token}` }
            });

            fetchHistory();
            fetchSaved();
        } catch (error) {
            setError('Failed to update save status');
        }
    };

    const handleFileSelect = (event) => {
        const file = event.target.files[0];
        setSelectedFile(file);
        setError(null);
        setImageUrl(''); // Clear URL input when file is selected

        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setPreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        const file = e.dataTransfer.files[0];
        if (file && file.type.startsWith('image/')) {
            setSelectedFile(file);
            setError(null);
            setImageUrl(''); // Clear URL input when file is dropped

            const reader = new FileReader();
            reader.onloadend = () => {
                setPreview(reader.result);
            };
            reader.readAsDataURL(file);
        } else {
            setError('Please drop an image file');
        }
    };

    const handleUrlChange = (e) => {
        setImageUrl(e.target.value);
        setSelectedFile(null); // Clear file selection when URL is entered
        setPreview(null);
    };

    const handleUrlSubmit = async () => {
        if (!imageUrl?.trim()) {
            setError('Please enter an image URL');
            return;
        }

        // Basic URL validation
        try {
            new URL(imageUrl);
        } catch (e) {
            setError('Please enter a valid URL');
            return;
        }

        setLoading(true);
        setError(null);
        setResults(null);

        try {
            console.log('Sending URL:', imageUrl); // Debug log
            const token = localStorage.getItem('token');
            const headers = token ? { Authorization: `Bearer ${token}` } : {};
            const response = await axios.post('http://localhost:5000/upload', {
                url: imageUrl.trim()
            }, {
                headers: headers,
            });

            if (response.data) {
                setResults(response.data);
                setPreview(imageUrl); // Set the URL as preview
            } else {
                setError('No results received from the server');
            }
        } catch (err) {
            console.error('Error:', err);
            const errorMessage = err.response?.data?.error || err.message;
            setError(`Failed to analyze image: ${errorMessage}`);
        } finally {
            setLoading(false);
        }
    };

    const handleUpload = async () => {
        if (!selectedFile) {
            setError('Please select an image first');
            return;
        }

        setLoading(true);
        setError(null);

        const formData = new FormData();
        formData.append('image', selectedFile);
        const token = localStorage.getItem('token');
        const headers = token ? { Authorization: `Bearer ${token}` } : {};

        try {
            const response = await axios.post('http://localhost:5000/upload', formData, {
                headers: headers,
            });

            setResults(response.data);
        } catch (err) {
            console.error('Error:', err);
            setError('Failed to analyze image. Please try again. ' + err.message);
        } finally {
            setLoading(false);
        }
    };

    const ResultSection = ({ title, items, renderItem }) => {
        if (!items || items.length === 0) return null;
        return (
            <div className="result-section">
                <h3 className="result-title">{title}</h3>
                <div className="result-grid">
                    {items.map((item, index) => renderItem(item, index))}
                </div>
            </div>
        );
    };

    return (
        <div className="app-container">
            <header className="header">
                <div className="header-content">
                    <h1 className="text-xl font-bold">Vision Analyzer</h1>
                    <nav className="nav-links">
                        <a href="#features" className="nav-link">Features</a>
                        <a href="#about" className="nav-link">About</a>
                        <a href="mailto:kartikarora220803@gmail.com" className="nav-link">Contact Us</a>
                        <button onClick={toggleTheme} className="theme-toggle">
                            {theme === 'light' ? (
                                <>
                                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                                    </svg>
                                    Dark Mode
                                </>
                            ) : (
                                <>
                                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                                    </svg>
                                    Light Mode
                                </>
                            )}
                        </button>
                        {user ? (
                            <div className="user-info">
                                <span>Welcome, {user.username}!</span>
                                <button onClick={handleLogout}>Log Out</button>
                            </div>
                        ) : (
                            <button onClick={() => setShowLogin(true)}>Log In</button>
                        )}
                    </nav>
                </div>
            </header>

            {/* Login Modal */}
            {showLogin && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>Log In</h2>
                        <form onSubmit={handleLogin}>
                            <input
                                type="text"
                                placeholder="Username"
                                value={loginForm.username}
                                onChange={(e) => setLoginForm({...loginForm, username: e.target.value})}
                                required
                            />
                            <input
                                type="password"
                                placeholder="Password"
                                value={loginForm.password}
                                onChange={(e) => setLoginForm({...loginForm, password: e.target.value})}
                                required
                            />
                            <button type="submit">Log In</button>
                        </form>
                        <p>
                            Don't have an account?{' '}
                            <button 
                                className="link-button"
                                onClick={() => {
                                    setShowLogin(false);
                                    setShowRegister(true);
                                }}
                            >
                                Register Now
                            </button>
                        </p>
                        <button 
                            className="close-button"
                            onClick={() => setShowLogin(false)}
                        >

                        </button>
                    </div>
                </div>
            )}

            {/* Register Modal */}
            {showRegister && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>Register</h2>
                        <form onSubmit={handleRegister}>
                            <input
                                type="text"
                                placeholder="Username"
                                value={registerForm.username}
                                onChange={(e) => setRegisterForm({...registerForm, username: e.target.value})}
                                required
                            />
                            <input
                                type="email"
                                placeholder="Email"
                                value={registerForm.email}
                                onChange={(e) => setRegisterForm({...registerForm, email: e.target.value})}
                                required
                            />
                            <input
                                type="password"
                                placeholder="Password"
                                value={registerForm.password}
                                onChange={(e) => setRegisterForm({...registerForm, password: e.target.value})}
                                required
                            />
                            <button type="submit">Register</button>
                        </form>
                        <p>
                            Already have an account?{' '}
                            <button 
                                className="link-button"
                                onClick={() => {
                                    setShowRegister(false);
                                    setShowLogin(true);
                                }}
                            >
                                Log In
                            </button>
                        </p>
                        <button 
                            className="close-button"
                            onClick={() => setShowRegister(false)}
                        >

                        </button>
                    </div>
                </div>
            )}

            <main className="main-content">
                <section className="hero-section">
                    <h1 className="hero-title">Intelligent Image Analysis</h1>
                    <p className="hero-subtitle">
                        Upload an image or provide an image URL to get detailed insights using Google's advanced Vision AI technology.
                    </p>
                </section>

                <section className="upload-section">
                    <div 
                        className="file-input-container"
                        onDragOver={handleDragOver}
                        onDrop={handleDrop}
                    >
                        <input
                            type="file"
                            onChange={handleFileSelect}
                            accept="image/*"
                            className="file-input"
                            id="file-input"
                        />
                        <label htmlFor="file-input">
                            {preview ? (
                                <img src={preview} alt="Preview" className="preview-image" />
                            ) : (
                                <div>
                                    <p className="text-lg font-medium">Drag and drop an image here</p>
                                    <p className="text-sm text-gray-500">or click to select a file</p>
                                </div>
                            )}
                        </label>
                    </div>

                    <div className="mt-4">
                        <div className="url-input-container">
                            <input
                                type="text"
                                value={imageUrl}
                                onChange={handleUrlChange}
                                placeholder="Or enter image URL here"
                                className="url-input"
                            />
                            <button
                                onClick={handleUrlSubmit}
                                disabled={!imageUrl || loading}
                                className="upload-button"
                            >
                                {loading ? <span className="loading-spinner" /> : 'Analyze URL'}
                            </button>
                        </div>
                    </div>

                    <div className="mt-4 text-center">
                        {selectedFile && (
                            <button
                                onClick={handleUpload}
                                disabled={!selectedFile || loading}
                                className="upload-button"
                            >
                                {loading ? <span className="loading-spinner" /> : 'Analyze Image'}
                            </button>
                        )}
                    </div>

                    {error && (
                        <div className="error-message">
                            {error}
                        </div>
                    )}
                </section>

                {results && (
                    <div className="results-container">
                        <ResultSection
                            title="Labels"
                            items={results.labels}
                            renderItem={(item, index) => (
                                <div key={index} className="result-item">
                                    <div className="font-medium">{item.description}</div>
                                    <div className="confidence-bar">
                                        <div 
                                            className="confidence-fill"
                                            style={{ width: `${item.confidence}%` }}
                                        />
                                    </div>
                                    <div className="text-sm text-gray-600">{item.confidence}% confidence</div>
                                </div>
                            )}
                        />

                        <ResultSection
                            title="Objects"
                            items={results.objects}
                            renderItem={(item, index) => (
                                <div key={index} className="result-item">
                                    <div className="font-medium">{item.name}</div>
                                    <div className="confidence-bar">
                                        <div 
                                            className="confidence-fill"
                                            style={{ width: `${item.confidence}%` }}
                                        />
                                    </div>
                                    <div className="text-sm text-gray-600">{item.confidence}% confidence</div>
                                </div>
                            )}
                        />

                        {results.faces.length > 0 && (
                            <div className="result-section">
                                <h3 className="result-title">Faces</h3>
                                <div className="result-grid">
                                    {results.faces.map((face, index) => (
                                        <div key={index} className="result-item">
                                            <div className="text-sm">
                                                <div>Joy: {face.joy}</div>
                                                <div>Sorrow: {face.sorrow}</div>
                                                <div>Anger: {face.anger}</div>
                                                <div>Surprise: {face.surprise}</div>
                                                <div className="confidence-bar">
                                                    <div 
                                                        className="confidence-fill"
                                                        style={{ width: `${face.confidence}%` }}
                                                    />
                                                </div>
                                                <div className="mt-1 font-medium">{face.confidence}% confidence</div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        <ResultSection
                            title="Landmarks"
                            items={results.landmarks}
                            renderItem={(item, index) => (
                                <div key={index} className="result-item">
                                    <div className="font-medium">{item.name}</div>
                                    <div className="confidence-bar">
                                        <div 
                                            className="confidence-fill"
                                            style={{ width: `${item.confidence}%` }}
                                        />
                                    </div>
                                    <div className="text-sm text-gray-600">{item.confidence}% confidence</div>
                                </div>
                            )}
                        />

                        {results.text && (
                            <div className="result-section">
                                <h3 className="result-title">Text Found</h3>
                                <div className="bg-gray-50 p-4 rounded-lg">
                                    <pre className="whitespace-pre-wrap">{results.text}</pre>
                                </div>
                            </div>
                        )}

                        <ResultSection
                            title="Web Entities"
                            items={results.webEntities}
                            renderItem={(item, index) => (
                                <div key={index} className="result-item">
                                    <div className="font-medium">{item.description}</div>
                                    <div className="confidence-bar">
                                        <div 
                                            className="confidence-fill"
                                            style={{ width: `${item.confidence}%` }}
                                        />
                                    </div>
                                    <div className="text-sm text-gray-600">{item.confidence}% confidence</div>
                                </div>
                            )}
                        />
                    </div>
                )}
            </main>

            <footer className="footer">
                <div className="footer-content">
                    <div>
                        <p>&copy; 2025 Vision Analyzer. All rights reserved.</p>
                    </div>
                    <div className="social-links">
                        <a href="mailto:kartikarora220803@gmail.com" className="social-link">
                            Contact Us
                        </a>
                        <a href="#privacy" className="social-link">
                            Privacy Policy
                        </a>
                        <a href="#terms" className="social-link">
                            Terms of Service
                        </a>
                    </div>
                </div>
            </footer>
        </div>
    );
}

export default App;
