import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [tags, setTags] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);

    // Create preview
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setError('Please select an image first');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('image', selectedFile);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      console.log('Response from server:', response.data);

      if (!response.data || !response.data.tags) {
        throw new Error('Invalid response format from server');
      }

      const parsedTags = response.data.tags;

      // Sort tags by confidence
      parsedTags.sort((a, b) => b.confidence - a.confidence);
      setTags(parsedTags);
    } catch (err) {
      console.error('Full error details:', err);
      if (err.response) {
        console.log('Error response:', err.response.data);
        setError('Server error: ' + JSON.stringify(err.response.data));
      } else {
        setError('Failed to analyze image. Please try again. Error: ' + err.message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Image Analysis</h1>

      <div className="mb-6">
        <input
          type="file"
          onChange={handleFileSelect}
          accept="image/*"
          className="mb-4"
        />

        {preview && (
          <div className="mb-4">
            <img src={preview} alt="Preview" className="max-w-md rounded-lg shadow-lg" />
          </div>
        )}

        <button
          onClick={handleUpload}
          disabled={!selectedFile || loading}
          className={`px-4 py-2 rounded-lg ${
            !selectedFile || loading
              ? 'bg-gray-300 cursor-not-allowed'
              : 'bg-blue-500 hover:bg-blue-600 text-white'
          }`}
        >
          {loading ? 'Analyzing...' : 'Analyze Image'}
        </button>
      </div>

      {error && (
        <div className="text-red-500 mb-4">{error}</div>
      )}

      {tags.length > 0 && (
        <div>
          <h2 className="text-xl font-semibold mb-4">Analysis Results</h2>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2">
            {tags.map((tag, index) => (
              <div 
                key={index}
                className="bg-gray-100 p-2 rounded-lg flex justify-between items-center"
                style={{
                  opacity: Math.max(0.5, tag.confidence / 100)
                }}
              >
                <span className="font-medium">{tag.name}</span>
                <span className="text-sm text-gray-600">{tag.confidence}%</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
